"use strict";var MessageFormat = require('messageformat');

var mf = new MessageFormat('en');

function success(data) {
  var files = `{count, plural, one{file} other{files}}`;
  return mf.compile(
  `{success} formatting {countString} ${files} with prettier-standard`)(
  data);
}

function failure(data) {
  var files = `{count, plural, one{file} other{files}}`;
  return mf.compile(
  `{failure} formatting {countString} ${files} with prettier-standard`)(
  data);
}

function unchanged(data) {
  var files = `{count, plural, one{file was} other{files were}}`;
  return mf.compile(`{countString} ${files} {unchanged}`)(data);
}

module.exports = {
  success,
  failure,
  unchanged };